import React, { Component } from 'react';
import apiKey from './config';
import './App.css';
import axios from 'axios';
import {
  BrowserRouter,
  Route,
  Switch
} from 'react-router-dom'

import Search from './components/Search';
import Gallery from './components/Gallery';
import Nav from './components/Nav';
import NotFound from './NotFound';


export default class App extends Component {

  constructor() {
    super();
    this.state = {
      pictures: [],
      oceans: [],
      rivers: [],
      lakes: [],
      loading: true
    }
  }

  componentDidMount() {
    this.performSearch();
  }

  performSearch = (searchItem = 'lakes') => {
    axios.get(`https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=${searchItem}&per_page=24&format=json&nojsoncallback=1`)
      .then(response => {
        this.setState({
          pictures: response.data.photos.photo,
          loading: false
        });
      })
      .catch(error => {
      console.log('Error fetching and parsing data', error);
      });
  }


  render() {
    return (
        <BrowserRouter>
          <div className="container">   
              <Route exact path = "/" component={() => <Search onSearch={this.performSearch} />} />
              <Route exact path="/lakes" component={() => <Search onSearch={this.lakeSearch} />}/>
            <Nav />
              <Search onSearch={this.performSearch} />
            <Switch> 
            {
              (this.state.loading)
                ? <h3 className='active'>Loading...</h3>
                : <Route exact path='/' render={() => <Gallery data={this.state.pictures} />} />
            }

            <Route path= '/lakes' render={() => <Gallery data={this.state.lakes} />} />
            <Route component={NotFound} />
            </Switch>
          </div>
        </BrowserRouter>


    );
  }
}
