import React, { Component } from 'react';
import apiKey from './config';
import './App.css';
import axios from 'axios';
import {
  BrowserRouter,
  Route,
  Switch
} from 'react-router-dom'

import Search from './components/Search';
import Gallery from './components/Gallery';
import NotFound from './components/NotFound';
import Nav from './components/Nav';
import NoPic from './components/NoPic';

export default class App extends Component {

  constructor() {
    super();
    this.state = {
      pictures: [],
      oceans: [],
      rivers: [],
      lakes: []
    }
  }

  componentDidMount() {
    this.performSearch();
  }

  performSearch = (searchItem = 'lakes') => {
    axios.get(`https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=${searchItem}&limit=24&format=json&nojsoncallback=1`)
      .then(response => {
        this.setState({
          pictures: response.data.data
        });
      })
      .catch(error => {
      console.log('Error fetching and parsing data', error);
      });
  }


  render() {
    console.log(this.state.pictures);
    return (

        <BrowserRouter>
        <div className="container">
          <Switch>
            <Route exact path = "/" component={Search} />
            <Route exact path = "/" component={Nav} />
            <Route exact path = "/" component={Gallery} />
            <Route component = {NotFound} />
            <Route path = "/no-pictures-available" component={NoPic} />
            <Search onSearch={this.performSearch} />
          </Switch>
          <Route path= '/lakes' render={() => <Gallery data={this.state.pictures} />} />
        </div>
        </BrowserRouter>


    );
  }
}
